mapper.select:
select c.classes_id, c.classes_name, c.creator_id, c.introduction, c.people_num, u.nick_name as creator_name from t_classes c left join user_classes uc on c.classes_id = uc.classes_id left join t_user u on u.user_id = c.creator_id where uc.user_id = #{userId}
AND c.classes_name like concat('%', #{classes.classesName}, '%')
AND u.user_name like concat('%', #{classes.creatorName}, '%')

select u.user_id, u.nick_name, u.sex, u.phone_number  from t_user u
left join user_classes uc on u.user_id = uc.user_id
where uc.classes_id = #{classesId}
